INSERT Items(ID,Titel,Categorie,Postcode,Locatie,Land,Verkoper,Prijs,Valuta,Conditie,Thumbnail,Beschrijving) VALUES (270762075586,'ALPINESTARS CARBON BIONIC NECK BRACE SUPPORT RED SM',82458,'9280','België','BE','sixstarracing*com','325.0','EUR','Nieuw met labels','img270762075586.jpg','<P><FONT size=2 face=Verdana><STRONG>Brand/marque&nbsp;:</STRONG>&nbsp;ALPINESTARS</FONT></P>
<P><FONT size=2 face=Verdana><STRONG>Name/nom :</STRONG> BIONIC NECK SUPPORT "CARBON"</FONT></P>
