<script type="text/javascript">
	// Ebay Artikelnummer holen
	
	function getEbayItemID(){		 	
		
		var CurUrl = document.URL;
		var CurValue = "";			
		var CurPos = -1;	
		
		CurValue = CurUrl ;
		nuValue =  CurValue.substring(CurValue.lastIndexOf("/")+1,CurValue.length - 1);	  
	  
		if (isNaN(nuValue) == false && nuValue != '''')		{
			return  nuValue;
		}		
		
		CurValue = "";		
		CurPos = CurUrl.indexOf("?");		
		
		while (CurPos > 0 ) {
				
			CurValue = CurUrl.substring(0,CurPos);
			nuValue =  CurValue.substring(CurValue.lastIndexOf("/")+1,CurValue.length - 1);

			if (isNaN(nuValue) == false && nuValue != ''''){
				return  nuValue;				
			}	
											
			CurUrl = CurUrl.substring(CurPos + 2);
			CurPos = CurUrl.indexOf("?");	
		}

		nuValue = "";
		return  nuValue;
	}
	
	
	function get_url_param( name )
	{
	
		var regexS = "[\\?&]"+name+"=([^&#]*)";
		var regex = new RegExp( regexS );
		var results = regex.exec( document.URL );

		if ( results == null )
			return "";
		else
			return results[1];
	}
	
	ebayItemID  = getEbayItemID();

	if (ebayItemID == '''')
	{
	  ebayItemID = get_url_param("item");
	}	
</script>

