        var itemsHTML = "";
        for(var i = 0; i < numItemsToCreate; i++){
          itemsHTML += createItemHTML(items[i % items.length]);
        }
        showCase = $(SHOW_CASE_ID);
        pane = $(PANE_ID);
        pane.innerHTML = itemsHTML;
        addEvent(pane,''mouseover'',stop);
        addEvent(pane,''mouseout'',start);
        start();
      }
    }else{
      $(SHOW_CASE_DIV_ID).style.display = "none";
    }
  }
  function createItemHTML(item){
    var title = String(item.title);
    var poundIdx = title.indexOf(''£'');
    while(poundIdx >= 0){
      title = title.substring(0,poundIdx) + "&pound;" + title.substring(poundIdx + 1);
      poundIdx = title.indexOf(''£'');
    }
    var subtitle = item.subtitle;
    var picURL = item.galleryURL;
    var itemURL = item.viewItemURL;
    var price = item.sellingStatus[0].convertedCurrentPrice[0].__value__;
