<script type="text/javascript">
function getPositionValue(el){
  if(el.currentStyle){
    return el.currentStyle["position"];
  }
  return window.getComputedStyle(el).getPropertyValue("position");
}
function getOffsetLeft(el){
  var intOffsetLeft = el.offsetLeft;
  el = el.offsetParent;
  while(el && getPositionValue(el) === "static"){
    intOffsetLeft += el.offsetLeft;
    el = el.offsetParent;
  }
  return intOffsetLeft;
}
function getOffsetTop(el){
  var intOffsetTop = el.offsetTop;
  el = el.offsetParent;
  while(el && getPositionValue(el) === "static"){
    intOffsetTop += el.offsetTop;
    el = el.offsetParent;
  }
  return intOffsetTop;
}
function getScrollLeft(){
  var objBody = null;
  var scrollLeft = 0;
  if(document.all && !window.opera){
    objBody = (window.document.compatMode == "CSS1Compat")? window.document.documentElement : window.document.body || null;
  }else{
    objBody = document.documentElement;
  }
  if(window.pageXOffset){
    scrollLeft = window.pageXOffset;
  }else if(typeof objBody.scrollLeft == "number"){
    scrollLeft = objBody.scrollLeft;
  }
  return scrollLeft;
}
function getScrollTop(){
  var objBody = null;
  var scrollTop = 0;
  if(document.all && !window.opera){
    objBody = (window.document.compatMode == "CSS1Compat")? window.document.documentElement : window.document.body || null;
  }else{
    objBody = document.documentElement;
