<script type="text/javascript">
function getPositionValue(el){
  if(el.currentStyle){
    return el.currentStyle["position"];
  }
  return window.getComputedStyle(el).getPropertyValue("position");
}
function getOffsetLeft(el){
  var intOffsetLeft = el.offsetLeft;
  el = el.offsetParent;
  while(el && getPositionValue(el) === "static"){
    intOffsetLeft += el.offsetLeft;
    el = el.offsetParent;
  }
  return intOffsetLeft;
}
function getOffsetTop(el){
  var intOffsetTop = el.offsetTop;
  el = el.offsetParent;
  while(el && getPositionValue(el) === "static"){
    intOffsetTop += el.offsetTop;
    el = el.offsetParent;
  }
  return intOffsetTop;
}
function getScrollLeft(){
  var objBody = null;
  var scrollLeft = 0;
  if(document.all && !window.opera){
    objBody = (window.document.compatMode == "CSS1Compat")? window.document.documentElement : window.document.body || null;
  }else{
    objBody = document.documentElement;
  }
  if(window.pageXOffset){
    scrollLeft = window.pageXOffset;
  }else if(typeof objBody.scrollLeft == "number"){
    scrollLeft = objBody.scrollLeft;
  }
  return scrollLeft;
}
function getScrollTop(){
  var objBody = null;
  var scrollTop = 0;
  if(document.all && !window.opera){
    objBody = (window.document.compatMode == "CSS1Compat")? window.document.documentElement : window.document.body || null;
  }else{
    objBody = document.documentElement;
  }
  if(window.pageYOffset){
    scrollTop = window.pageYOffset;
  }else if(typeof objBody.scrollTop == "number"){
    scrollTop = objBody.scrollTop;
  }
  return scrollTop;
}
function getClientWidth(){
  var clientWidth = document.documentElement.clientWidth;
  if(clientWidth == 0){
    clientWidth = document.body.clientWidth;
  }
  return clientWidth;
}
function toggleBubbleHelp(event,bubbleHelpId,el){
  if(event.bubbles === undefined){
    event.cancelBubble = true;
  }else{
    event.stopPropagation();
  }
  var bubbleHelp = document.getElementById(bubbleHelpId);
  if(bubbleHelp.style.visibility != "visible"){
    var offsetTop = getOffsetTop(el);
    bubbleHelp.style.top = (offsetTop - bubbleHelp.offsetHeight - 4) + "px";
    var offsetLeft = getOffsetLeft(el);
    var horizontalSpace = getScrollLeft() + getClientWidth() - offsetLeft;
    if(horizontalSpace > bubbleHelp.offsetWidth){
      bubbleHelp.style.left = offsetLeft + "px";
    }else{
      bubbleHelp.style.left = (offsetLeft + el.offsetWidth - bubbleHelp.offsetWidth) + "px";
    }
    bubbleHelp.style.visibility = "visible";
  }else{
    bubbleHelp.style.visibility = "hidden";
  }
}
function closeBubbleHelp(bubbleHelpId){
    if ($(bubbleHelpId)){
      $(bubbleHelpId).style.visibility = ''hidden'';
      return false;
    }
}
