<script type="text/javascript">
var eMPShowcase = function(){
  var SHOW_CASE_DIV_ID = "emp_showcase";
  var SHOW_CASE_ID = "emp_sc";
  var PANE_ID = "emp_sc_pane"
  var ITEM_WIDTH = 148;
  var ITEM_HEIGHT = 208;
  var ITEM_PADDING = 6;
  var MIN_ITEMS = 8;
  var CONTAINER_WIDTH = 1000;
  var MOVE_INC = 1;
  var MOVE_INTERVAL = 30;
  var showCase;
  var pane;
  var moveTimer;
  var numItems;
  /* helper functions */
  function $(id) {return document.getElementById(id); }
  function addEvent(obj, type, fn) {
    if(obj.addEventListener) {
      obj.addEventListener(type,fn,false);
    }else if(obj.attachEvent) {
      obj["e" + type + fn] = fn;
      obj[type + fn] = function() {obj["e" + type + fn](window.event);}
      obj.attachEvent("on" + type, obj[type + fn]);
    }
  }
  /* helper functions end */
  function init(response){
    if(response.findItemsAdvancedResponse[0].searchResult){
      var items = response.findItemsAdvancedResponse[0].searchResult[0].item;
      if(items.length == 1){
        $(SHOW_CASE_DIV_ID).style.display = "none";
      }else{
        numItems = items.length;
        var numItemsToCreate = MIN_ITEMS > items.length ? MIN_ITEMS : items.length;
        numItemsToCreate += MIN_ITEMS;
        var itemsHTML = "";
        for(var i = 0; i < numItemsToCreate; i++){
          itemsHTML += createItemHTML(items[i % items.length]);
        }
        showCase = $(SHOW_CASE_ID);
        pane = $(PANE_ID);
        pane.innerHTML = itemsHTML;
        addEvent(pane,''mouseover'',stop);
        addEvent(pane,''mouseout'',start);
        start();
      }
    }else{
      $(SHOW_CASE_DIV_ID).style.display = "none";
    }
  }
  function createItemHTML(item){
    var title = String(item.title);
    var poundIdx = title.indexOf(''£'');
    while(poundIdx >= 0){
      title = title.substring(0,poundIdx) + "&pound;" + title.substring(poundIdx + 1);
      poundIdx = title.indexOf(''£'');
    }
    var subtitle = item.subtitle;
    var picURL = item.galleryURL;
    var itemURL = item.viewItemURL;
    var price = item.sellingStatus[0].convertedCurrentPrice[0].__value__;
    var price = price.substring(0,price.indexOf(''.0''));
    var itemHTML = "<div class=''emp_sci''>";
    itemHTML += "<div class=''emp_sci_pic''>";
    if(picURL){
      itemHTML += "<a href=''" + itemURL + "'' target=''_blank''><img border=''0'' src=''" + picURL + "''><\/a>";
    }
    itemHTML += "<\/div>";
    itemHTML += "<div class=''emp_sci_title''>" + title + "<\/div>";
    itemHTML += "<div class=''emp_sci_price''>&pound;" + price + "<\/div>";
    itemHTML += "<\/div>";
    return itemHTML;
  }
  function start(){
    stop();
    moveTimer = window.setInterval(move, MOVE_INTERVAL);
  }
  function stop(){
    window.clearInterval(moveTimer);
  }
  var direction = -1;
  var currPos = 0;
  function move(){
    if(currPos <= -(ITEM_WIDTH * numItems)){
      currPos = 0;
    }
    currPos +=  -1 * MOVE_INC;
    pane.style.left = currPos + ''px'';
  }
  return{
    init:init
  }
}();
</script>
<script type="text/javascript">
function getPositionValue(el){
  if(el.currentStyle){
    return el.currentStyle["position"];
  }
  return window.getComputedStyle(el).getPropertyValue("position");
}
function getOffsetLeft(el){
  var intOffsetLeft = el.offsetLeft;
  el = el.offsetParent;
  while(el && getPositionValue(el) === "static"){
    intOffsetLeft += el.offsetLeft;
    el = el.offsetParent;
  }
  return intOffsetLeft;
}
function getOffsetTop(el){
  var intOffsetTop = el.offsetTop;
  el = el.offsetParent;
  while(el && getPositionValue(el) === "static"){
    intOffsetTop += el.offsetTop;
    el = el.offsetParent;
  }
  return intOffsetTop;
}
function getScrollLeft(){
  var objBody = null;
  var scrollLeft = 0;
  if(document.all && !window.opera){
    objBody = (window.document.compatMode == "CSS1Compat")? window.document.documentElement : window.document.body || null;
  }else{
    objBody = document.documentElement;
  }
  if(window.pageXOffset){
    scrollLeft = window.pageXOffset;
  }else if(typeof objBody.scrollLeft == "number"){
    scrollLeft = objBody.scrollLeft;
  }
  return scrollLeft;
}
function getScrollTop(){
  var objBody = null;
  var scrollTop = 0;
  if(document.all && !window.opera){
    objBody = (window.document.compatMode == "CSS1Compat")? window.document.documentElement : window.document.body || null;
  }else{
    objBody = document.documentElement;
  }
  if(window.pageYOffset){
    scrollTop = window.pageYOffset;
  }else if(typeof objBody.scrollTop == "number"){
    scrollTop = objBody.scrollTop;
  }
  return scrollTop;
}
function getClientWidth(){
  var clientWidth = document.documentElement.clientWidth;
  if(clientWidth == 0){
    clientWidth = document.body.clientWidth;
  }
  return clientWidth;
}
function toggleBubbleHelp(event,bubbleHelpId,el){
  if(event.bubbles === undefined){
    event.cancelBubble = true;
  }else{
    event.stopPropagation();
  }
  var bubbleHelp = document.getElementById(bubbleHelpId);
  if(bubbleHelp.style.visibility != "visible"){
    var offsetTop = getOffsetTop(el);
    bubbleHelp.style.top = (offsetTop - bubbleHelp.offsetHeight - 4) + "px";
    var offsetLeft = getOffsetLeft(el);
    var horizontalSpace = getScrollLeft() + getClientWidth() - offsetLeft;
    if(horizontalSpace > bubbleHelp.offsetWidth){
      bubbleHelp.style.left = offsetLeft + "px";
    }else{
      bubbleHelp.style.left = (offsetLeft + el.offsetWidth - bubbleHelp.offsetWidth) + "px";
    }
    bubbleHelp.style.visibility = "visible";
  }else{
    bubbleHelp.style.visibility = "hidden";
  }
}
function closeBubbleHelp(bubbleHelpId){
    if ($(bubbleHelpId)){
      $(bubbleHelpId).style.visibility = ''hidden'';
      return false;
    }
}
</script>
