INSERT Items(ID,Titel,Categorie,Postcode,Locatie,Land,Verkoper,Prijs,Valuta,Conditie,Thumbnail,Beschrijving) VALUES (231394199927,'1970 Buick Riviera - Direkt aus den USA - Sehr gutes Auto',29750,'91724','Verenigde Staten','US','rostfreillc','1.0','EUR','Tweedehands','img231394199927.jpg','<div align="center"><b><i><u><font size="5">1970 Buick Riviera</font><br></u></i></b></div><br><div align="center"><font color="#FF0010"><i><font size="4">Kein Mindestpreis, der Hoechstbietende gekommt den Riviera.</font></i></font> <font size="4"><i><font color="#FF0010">Bitte bieten Sie nur mit, wenn Sie auch das Geld fuer das Auto haben.</font></i></font><br><br><i><font size="4">Der Wagen steht bei uns in Los Angeles. Bei Interesse lesen Sie sich bitte die komplette Auktion durch.</font></i><br><br><i><font size="4">Wir sind seit ueber 7 Jahren taeglich auf den Highways der USA unterwegs, immer auf der Suche nach guten Oldtimern. Diesmal haben wir einen sehr guten 1970 Buick Riviera gefunden. </font></i><br><br><i><font size="4">Wir probieren das Auto so ehrlich wie moeglich zu Beschreiben. Diese Auktion beinhaltet detalierte Fotos.</font></i><br><br><i><font size="4">VIN/FIN: 49487OH929492</font></i><br><br><a href="http://s272.photobucket.com/user/RostfreiLLC/media/1970%20Riviera/P1030475_zps65cb4217.jpg.html" target="_blank"><img src="http://i272.photobucket.com/albums/jj172/RostfreiLLC/1970%20Riviera/P1030475_zps65cb4217.jpg" alt=" photo P1030475_zps65cb4217.jpg" border="0"></a><br><br><i><font size="4">Es handelt sich hier um ein echtes "California Car". Wir sind immer wieder erstaunt, dass es noch Oldtimer in diesem Zustand gibt. Der Wagen hat absolut keine Durchrostungen und wir haben nur eine, ca 2x2cm Spachtelstelle in der ganzen Karosserie gefunden. Einfach nur Klasse. Das Lackkleid (keine Hinterhof Lackierung) praesentiert sich in einem sehr gutem Zustand, bis auf zwei Stellen, die Beilackiert werden muessen. Der Wagen hat in 2012 ein neues Vinyldach erhalten, dabei kamen zwei tiefe Kratzer unterhalb der Heckscheibe in den Lack. Der letzte Vorbesitzer hat die beiden Stellen Grundiert, so dass keine Chance fuer Rost ensteht. Man sieht sofort, dass der Wagen von Tag 1 an gepflegt wurde. Keinerlei Anzeichen auf jegliche Unfallschaeden. Keine Beulen, kerzengerade. Spaltmasse passen zu 100%. Chrom ist sehr schoen. Keinerlei Rost unterm Dinyldach. Alle Scheiben sind frei von defekten. Aber bitte sehen Sie selbst, hier eine Auswahl an Bildern, die das aeussere wiederspiegeln.</font></i><br><br></div><div align="center"><a href="http://s272.photobucket.com/user/RostfreiLLC/media/1970%20Riviera/P1030476_zps3eb10bca.jpg.html" target="_blank"><img src="http://i272.photobucket.com/albums/jj172/RostfreiLLC/1970%20Riviera/P1030476_zps3eb10bca.jpg" alt=" photo P1030476_zps3eb10bca.jpg" border="0"></a><br></div><br><div align="center"><a href="http://s272.photobucket.com/user/RostfreiLLC/media/1970%20Riviera/P1030477_zps6a88dc74.jpg.html" target="_blank"><img src="http://i272.photobucket.com/albums/jj172/RostfreiLLC/1970%20Riviera/P1030477_zps6a88dc74.jpg" alt=" photo P1030477_zps6a88dc74.jpg" border="0"></a><br></div><br><div align="center"><a href="http://s272.photobucket.com/user/RostfreiLLC/media/1970%20Riviera/P1030459_zps865eb3a8.jpg.html" target="_blank"><img src="http://i272.photobucket.com/albums/jj172/RostfreiLLC/1970%20Riviera/P1030459_zps865eb3a8.jpg" alt=" photo P1030459_zps865eb3a8.jpg" border="0"></a><br></div><br><div align="center"><a href="http://s272.photobucket.com/user/RostfreiLLC/media/1970%20Riviera/P1030461_zps0df0c760.jpg.html" target="_blank"><img src="http://i272.photobucket.com/albums/jj172/RostfreiLLC/1970%20Riviera/P1030461_zps0df0c760.jpg" alt=" photo P1030461_zps0df0c760.jpg" border="0"></a><br></div><br><div align="center"><a href="http://s272.photobucket.com/user/RostfreiLLC/media/1970%20Riviera/P1030456_zps8def0494.jpg.html" target="_blank"><img src="http://i272.photobucket.com/albums/jj172/RostfreiLLC/1970%